<?php// this controller needs Twig$twig = start_twig('comments');if (isset($request[1]) && $request[1] != ''){	switch($request[1])	{		case "new":			include "new.php";			break;	}}else{	// default route /comments	if ($id != '' && is_numeric($id))	{		require_once('course/ajax/get_comments.php');		echo $twig->render('_comments_ajax.html', array(			'logged_in' => $loggedIn,			'progress' => percentage($uvanetid),			'url' => urlencode($url),			'username' => $name,			'uvanetid' => $uvanetid,			'admin' => isAdmin($uvanetid),			'comments' => $comments		));	}	else	{		header("Status: 404 Not Found");		echo "404";	}	break;}