<?php// this controller needs Twig$twig = start_twig('comments');if (isset($request[1])){	switch($request[0])	{		case "replies":					if ($id != '' && is_numeric($id))			{				require_once('course/ajax/get_replies.php');				echo $twig->render('_replies_ajax.html', array(					'logged_in' => $loggedIn,					'progress' => percentage($uvanetid),					'url' => urlencode($url),					'username' => $name,					'id' => $id,					'uvanetid' => $uvanetid,					'admin' => isAdmin($uvanetid),					'comment' => array('','','','','', $replies)				));			}			else			{				header("Status: 404 Not Found");				echo "404";			}			break;				case "new":			include "new_comment.php";			break;	}		$id = $request[1];	$folderid = $request[1];}else{	$id = '';	$folderid = '';}